import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";


const useStyles = makeStyles((theme) => ({ 

  welcome_page : {
    background: 'linear-gradient( to right, white 0%, white 50%, black 50%,black 100%)',
  },

  text : {
    color: 'white',
    fontSize: '5rem',
    // position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    cursor: 'pointer',
    '&::before': {
      content: 'attr(data-heading)',
      position: 'absolute',
      left: '0',
      color: 'black',
      width: '50%',
      overflow: 'hidden',
    }
  }

}))

export const Welcome = () => {
  const classes = useStyles();
  return (
    <Box height="calc(100vh - 65px)" className={classes.welcome_page}>
      <Box position= 'absolute'>
      <Typography fontSize="5rem" whiteSpace="nowrap" className={classes.text} data-heading="WELCOME">
        WELCOME
      </Typography>
      </Box>
    </Box>
  );
};
