    backgroundColor: "#2B2D42", 
    padding: "10px 20px"
  },
  box : {
    display: "flex",
    alignItems: "center",
    gap: "30px",
  },
  link : {
    color : "white",
    textDecoration: "none",
    paddingBottom: "5px",
    borderBottom: "2px solid transparent",
    transition: "0.2s all linear",
    textAlign: "center",
    fontSize: "18px",
    '&:hover' : {
      color        : "white",
      borderBottom : "2px solid #ff8120"
    }
  }
}));


