import React, { useEffect, useState } from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";

import "./Login.css";
import { useNavigate } from "react-router-dom";

const useStyles = makeStyles((theme) => ({
  container: {
    backgroundImage: 'url("/images/login.jpg")',
    width: "100%",
    height: "calc(100vh - 65px)",
    backgroundSize: "cover",
    position: "relative",
  },
  form: {
    backgroundColor: "#2b2d42",
    boxShadow: "0 10px 20px rgba(0, 0, 0, 0.19), 0 3px 6px rgba(0, 0, 0, 0.23)",
    width: "40%",
    padding: "20px",
    position: "absolute",
    left: "50%",
    top: "40%",
    transform: "translate(-50%, -40%)",
    borderRadius: "5px",
  },
  title: {
    textAlign: "center",
    fontFamily: "Arial, Helvetica, sans-serif",
    fontSize: "32px",
    marginBottom: "15px",
    color: "white",
  },
  loginForm: {
    marginBottom: "20px",
    width: "100%",
    display: "flex",
    justifyContent: "center",
    flexWrap: "wrap",
    position: "relative",
  },
  login_form_input : {
    width: "90%",
    margin: "auto",
  }
}));

export const Login = (props) => {
  const classes = useStyles();

  const navigate = useNavigate();

  const [users, setUsers] = useState([]);

  const [{ email, password }, setLoginForm] = useState({
    email: "",
    password: "",
  });

  const [error, setError] = useState("");

  useEffect(() => {
    setUsers(() => {
      return JSON.parse(localStorage.getItem("users"));
    });
  }, []);

  const loginValuesHandler = (event) => {
    setLoginForm((preState) => {
      return { ...preState, [event.target.name]: event.target.value };
    });
  };

  const loginFormHandler = (event) => {
    event.preventDefault();
    if (email.trim().length === 0 || password.trim().length === 0) {
      setError("Please fill in all inputs");
    } else if (users) {
      let user = users.filter((ele, index) => {
        return ele.email === email;
      })[0];
      if (user) {
        if (user.password === password) {
          props.setLoggedIn(true);
          localStorage.setItem("userLoggedIn", JSON.stringify(user));
          props.setUserLoggedIn(user);
          navigate("/");
        } else {
          setError("Password is not correct");
        }
      } else {
        setError("Email is not exist");
      }
    } else {
      setError("Email is not exist");
    }
  };

  return (
    <Container className={classes.container}>
      <form className={classes.form} autoComplete="off">
        <Typography className={classes.title}>Login</Typography>
        <Box className={classes.loginForm}>
          <TextField
            data-testid="email"
            onChange={loginValuesHandler}
            value={email}
            name="email"
            className={login_form_input }
            id="standard-basic"
            label="Email"
            InputLabelProps={{ style: { color: "#ddd" } }}
          />
        </Box>
        <Box className={classes.loginForm}>
          <TextField
            data-testid="password"
            onChange={loginValuesHandler}
            value={password}
            name="password"
            type="password"
            className="login-form_input"
            id="standard-basic"
            label="Password"
            InputLabelProps={{ style: { color: "#ddd" } }}
            style={{ color: "white !important" }}
          />
          <div
            className={` ${
              error ? "login-error-opacity" : ""
            } error login-error`}
          >
            {error ? error : "Please fill in all inputs"}
          </div>
        </Box>
        <Button
          onClick={loginFormHandler}
          id="login-btn"
          variant="contained"
          color="primary"
        >
          Login
        </Button>
      </form>
    </Container>
  );
};
